import { getSunTime } from "../../helpers";
import Sunrise from "../Icons/Sunrise";
import Sunset from "../Icons/Sunset";
import { forecastType } from "../Icons/types";

type Props = {
  data: forecastType;
};

const Degree = ({ temp }: { temp: number }): JSX.Element => (
  <span>
    {Math.round(temp) - 271}
    <sup>0</sup>
  </span>
);

const Forecast = ({ data }: Props): JSX.Element => {
  const today = data.list[0];
  return (
    <div className="w-full md:max-w-[500px] py-4 md:py-4 md:px-10 lg:px-24 h-full lg:h-auto bg-white bg-opacity-20 backdrop-blur-ls rounded drop-shadow-lg">
      <div className="mx-auto w-[300px]">
        <section className="text-center">
          <h2 className="text-2xl font-black">
            {data.name}, <span className="font-thin">{data.country}</span>
          </h2>
          <h1 className="text-4xl font-extrabold">
            <Degree temp={today.main.temp} />
          </h1>
          <p className="text-sm">
            {today.weather[0].main}ãƒ»{today.weather[0].description}
          </p>
          <p className="text-sm">
            H: <Degree temp={Math.ceil(today.main.temp_max)} />
            L: <Degree temp={Math.floor(today.main.temp_min)} />
          </p>
        </section>
        <section className="flex overflow-x-scroll mt-4 pb-2 mb-5">
          {data.list.map((item, i) => {
            return (
              <div
                className="inline-block text-center w-[50px] flex-shrink-0"
                key={i}>
                <p className="text-sm ">
                  {i === 0 ? "Now" : new Date(item.dt * 1000).getHours()}
                </p>
                <img
                  src={`https://openweathermap.org/img/wn/10d@2x.png`}
                  alt={`weather-icon-${item.weather[0].description}`}
                />
                <Degree temp={item.main.temp} />
              </div>
            );
          })}
        </section>

        {/* <section className="flex justify-between tetx-zinc-700">
          <div className="w-[140px] text-xs font-bold flex flex-col items-center bg-white/20 backdrop-blur-lg rounded drop-shadow-lg py-4 mb-5">
            <Sunrise />
            <span className="mt-2">{getSunTime(data.sunrise)}</span>
          </div>

          <div className="w-[140px] text-xs font-bold flex flex-col items-center bg-white/20 backdrop-blur-lg rounded drop-shadow-lg py-4 mb-5">
            <Sunset />
            <span className="mt-2">{getSunTime(data.sunset)}</span>
          </div>
        </section> */}
      </div>
    </div>
  );
};

export default Forecast;
