import { ChangeEvent, useState } from "react";

//
//every react comp need to be define
const App = (): JSX.Element => {
  const [term, setTerm] = useState<string>("");
  const [option, setoption] = useState<[]>([]);

  const getSearchOption = (value: string) => {
    fetch(
      `http://api.openweathermap.org/geo/1.0/direct?q=${value.trim()}&limit=5&appid=f281ad23c454d69d7b050e565c983d52`
    )
      .then((res) => res.json())
      .then((data) => setoption(data));
  };

  //
  // e is an inplicit type, tell to the code that is expentig something
  const onChangeInput = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.trim();
    setTerm(value);

    if (value === "") return;

    getSearchOption(value);
    // console.log(value, "yo");
  };

  return (
    <main className="flex justify-center items-center bg-gradient-to-br from-sky-400 via-rose-400 to-lime-400 h-[100vh] w-full">
      <section className="bg-white w-full md:max-w-[500px] p-4 flex flex-col text-center items-center justify-center md:px-10 lg:p:24 h-full lg:h=[500px] bg-opacity-20 background-blur-lg drop-shadow-lg rounded text-zinc-700">
        <h1 className="text-4xl font-thin">
          Weather <span className="font-black">Forecast</span>
        </h1>
        <p className="text-sm mt-2">
          Enter below a place where you want to know the weather and selct an
          option from the dropdown
        </p>
        <div className="flex mt-10 md:mt-4">
          <input
            type="text"
            value={term}
            className="px-2 py-1 border-2 border-white"
            onChange={onChangeInput}
          />
          <button className="rounded-r-md border-2 border-zinc-100 hover:border-zinc-500 hover:text-zinc-500 text-zinc-100 px-2 py-1 cursor-pointer">
            Search
          </button>
        </div>
      </section>
    </main>
  );
};

export default App;

